mapper.select:
select *
from blog_article
order by limit #{offset}, #{limit}

mapper.update:
update blog_article
set is_top = #{isTop}
where object_id = #{objectId}

update blog_article
set is_published = #{isPublished}
where object_id = #{objectId}

mapper.select:
select is_top
from blog_article
where object_id = #{objectId}

select is_published
from blog_article
where object_id = #{objectId}

mapper.update:
update blog_article
set commentable = #{commentable}
where object_id = #{objectId}

update blog_article
set view_password = #{viewPassword}
where object_id = #{objectId}

update blog_article
set comment_count = comment_count + 1
where object_id = #{objectId}

update blog_article
set view_count = view_count + 1
where object_id = #{objectId}
